tinymce.PluginManager.add('jbimages', function(editor, url) {
    function jbBox() {
        editor.windowManager.open({title: 'Загрузка изображений', file: url + '/dialog-v4.htm', width: 350, height: 135, buttons: [{text: 'Загрузить', classes: 'widget btn primary first abs-layout-item', disabled: true, onclick: 'Закрыть'}, {text: 'Закрыть', onclick: 'Закрыть'}]})
    }
//    editor.addButton('jbimages', {tooltip: 'Upload an image', icon: 'image', text: 'Upload', onclick: jbBox});
    editor.addMenuItem('jbimages', {text: 'Загрузка изображений', icon: 'image', context: 'insert', onclick: jbBox});

    editor.settings.file_browser_callback = function(id, value, type, win) {
        jbBox();
    };
});